      this.setState(
        {
          leftSideOpen: !this.state.leftSideOpen,
          rightSideOpen: !this.state.rightSideOpen,
        }
      );
    }
    else {
      this.setState(
        {
          leftSideOpen: !this.state.leftSideOpen,
        }
      );
    }
  }

