import React, { Component } from 'react';
import './App.css';

//import components
import Navbar from './components/Navbar'

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      leftSideOpen: false,
      rightSideOpen: false,
    }

  }

  toggleLeftSlide() {
    console.log(this.state.leftSideOpen)
    this.setState(
      {
        leftSideOpen: this.state.leftSideOpen ? false : true,
        rightSideOpen: false,
      }
    );
    console.log(this.state)

  }

   toggleRightSlide() {
    this.setState(
      {
        rightSideOpen: !this.state.rightSideOpen,
        leftSideOpen: false,
      }
    );
   }

  render() {
    return (
      <div className="App">
        <Navbar toggleLeft={this.toggleLeftSlide.bind(this)} leftMenuState={this.state.leftSideOpen} toggleRight={this.toggleRightSlide.bind(this)} rightMenuState={this.state.rightSideOpen} />
      </div>
    );
  }
}

export default App;
