import React from 'react';
import './index.scss'

import MailIcon from '../Icons/mail';

class Navbar extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            leftActive: null,
            rightActive: null,
        }
    }

    handleLeftClick() {
        this.props.toggleLeft();
    }

    handleRightClick() {
        this.props.toggleRight();
    }

    componentWillMount() {
        this.setState(
            {leftActive: this.props.leftMenuState,
             rightActive: this.props.rightMenuState}
        ); 
    }


    componentWillReceiveProps() {
        this.setState(
            {leftActive: this.props.leftMenuState,
             rightActive: this.props.rightMenuState}
        );
    }

    render() {
        return (
            <nav>
                <button className={this.state.leftActive ? 'left-active' : 'left-hidden'} onClick={this.handleLeftClick.bind(this)}>
                    <MailIcon/>
                </button>
                <a href='/#' title='logo' className='logo'>
                    <h1>APP</h1>                                
                </a>
                <button className={this.state.rightActive ? 'right-active' : 'right-hidden'} onClick={this.handleRightClick.bind(this)}>
                    <span/>
                    <span/>
                    <span/>
                </button>
            </nav>
        )
    }

}

export default Navbar;
